mapper.select:
SELECT COUNT(tc.id) showcount,tor.*,tre.`name` showuser,tc.number showname,

tc.photo showimg,tc.price showprice from t_order tor,t_commodity tc,

t_comm_order tco,t_region tre WHERE tor.linkuserid = #{value}

AND tor.orderid = tco.orderid and tco.commid = tc.id

and tre.userId = #{value}

GROUP BY tor.orderid ORDER BY tor.subtime

SELECT COUNT(tc.id) showcount,tor.*,tre.`name` showuser, tre.phone showphone,tre.sheng showsheng,tre.inDetail showdetail,tc.number showname,

tc.photo showimg,tc.price showprice from t_order tor,t_commodity tc,

t_comm_order tco,t_region tre WHERE tor.linkuserid =#{id}

AND tor.orderid = tco.orderid and tco.commid = tc.id

GROUP BY tor.orderid

and tre.userId =#{id}
and tor.orderid = #{orderid}

mapper.insert:
insert into t_comm_order (id, commid, orderid
  )
values (#{id}, #{commid}, #{orderid}
  )
